<script>
export default {
  props: {
    platform: String
  },
  data() {
    return {
      platformConfig: this.getGlobalConfig(),
      color: this.getColor()
    };
  },
  methods: {
    getImgUrl(p, small) {
      if (small) {
        var images = require.context(
          "../../static/images/brands/",
          false,
          /\.png$/
        );
        return images("./" + p + ".png");
      } else {
        p = p + "-lg";
        var images = require.context(
          "../../static/images/brands/",
          false,
          /\.png$/
        );
        return images("./" + p + ".png");
      }
    },
    getColor() {
      for (var p in this.$store.state["platforms"]) {
        if (p.name === this.$props.platform) {
          this.color = p.color;
        }
      }
    },
    getGlobalConfig() {
      let arr = this.$store.state.platforms;
      let obj = arr.find(o => o.name == this.$props.platform);
      return obj;
    }
  }
};
</script>
